mapper.update:
update chef_beliked
set
id= #{id},userId=#{userId},chefId=#{chefId},create_time=#{createTime},is_delete=#{isDelete}
where id=#{id}

mapper.select:
SELECT * FROM chef_beliked

    is_delete != -1 and userId=#{userId}

${sort}

    limit #{start},#{size}

SELECT * FROM chef_beliked

    is_delete != -1

        and userId = #{userId}


        and chefId = #{chefId}
